#include <gtest/gtest.h>
#include "count_number_of_texts.h"

TEST(Solution, testcase) {
  struct TestCase {
    string str;
    int cnt;
  };
  TestCase test_case[] = {
    {"2", 1},
    {"22233", 8},
    {"222222222222222222222222222222222222", 82876089},
    {"", 886136986},
    {"355577777788899", 928},
    {"9999333399993333788", 6272},
    {"9999", 8},
  };

  Solution solution;
  for (size_t i = 0; i < sizeof(test_case)/sizeof(test_case[0]); ++i) {
    EXPECT_EQ(solution.countTexts(test_case[i].str), test_case[i].cnt);
  }
}

